import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import { BrowserRouter as Router } from 'react-router-dom';

function Home() {
  const [showContact, setShowContact] = useState(false);
  const [showBackground, setShowBackground] = useState(false);

  // useRef to store the timer ID
  const timerRef = useRef(null);

  useEffect(() => {
    if (showBackground) {
      // Set the timer when background is shown
      timerRef.current = setTimeout(() => {
        setShowBackground(false);
        timerRef.current = null; // Clear the ref after timeout
      }, 5000);
    }

    // Cleanup function to clear the timer
    return () => {
      if (timerRef.current) {
        clearTimeout(timerRef.current);
        timerRef.current = null;
      }
    };
  }, [showBackground]);

  const toggleBackground = () => {
    if (showBackground) {
      // If background is already shown, hide it and clear the timer
      setShowBackground(false);
      if (timerRef.current) {
        clearTimeout(timerRef.current);
        timerRef.current = null;
      }
    } else {
      // If background is not shown, show it
      setShowBackground(true);
    }
  };

  const hideBackground = () => {
    setShowBackground(false);
    if (timerRef.current) {
      clearTimeout(timerRef.current);
      timerRef.current = null;
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <a
          href="https://www.instagram.com/willo_cc/?igsh=MzRlODBiNWFlZA%3D%3D"
          onClick={hideBackground}
        >
          <img
            src={`${process.env.PUBLIC_URL}/logo.png`}
            className="App-logo"
            alt="logo"
          />
        </a>
        <div className="contact-container">
          <span
            onClick={() => setShowContact(!showContact)}
            className="App-contact-text"
          >
            CONTACT
          </span>
          {showContact && (
            <div className="contact-info">
              <p>info@willow.cc</p>
            </div>
          )}
        </div>
      </header>

      <div className={`App-background ${showBackground ? "show" : ""}`}></div>

      <img
        src={`${process.env.PUBLIC_URL}/tree.png`}
        className="Tree-image"
        alt="Tree"
        onClick={toggleBackground}
      />

      <footer className="App-footer">
        <p>&copy; 2024 WILLO - All Rights Reserved.</p>
      </footer>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Home />
    </Router>
  );
}

export default App;
