import React, { useState } from 'react';
import './App.css';

function Home() {
  const [showContact, setShowContact] = useState(false);
  const [backgroundVisible, setBackgroundVisible] = useState(false);

  const toggleBackground = () => {
    setBackgroundVisible(true); // Set to true to start the transition
  };

  return (
    <div className="App">
      <header className="App-header">
        <a href="https://www.instagram.com/willo_cc/?igsh=MzRlODBiNWFlZA%3D%3D">
          <img
            src={`${process.env.PUBLIC_URL}/logo.png`}
            className="App-logo"
            alt="logo"
          />
        </a>
        <div className="contact-container">
          <span
            onClick={() => setShowContact(!showContact)}
            className="App-contact-text"
          >
            CONTACT
          </span>
          {showContact && (
            <div className="contact-info">
              <p>arman@willo.cc</p>
            </div>
          )}
        </div>
      </header>

      {/* Apply the 'show' class based on state */}
      <div className={`App-background ${backgroundVisible ? 'show' : ''}`}></div>

      <img
        src={`${process.env.PUBLIC_URL}/tree.png`}
        className="Tree-image"
        alt="Tree"
        onClick={toggleBackground} // Call the function on click
      />

      <footer className="App-footer">
        <p>&copy; 2024 WILLO - All Rights Reserved.</p>
      </footer>
    </div>
  );
}

export default Home;
